import React from 'react'
import VerifiedUser from "@mui/icons-material/VerifiedUser"
import "./Post.css";
import { Avatar } from '@mui/material';
import ChatBubbleOutline from "@mui/icons-material/ChatBubbleOutline";
import FavoriteBorder from "@mui/icons-material/FavoriteBorder";
import RepeatOutlined from "@mui/icons-material/RepeatOutlined"

function Post({displayName,Username,TimeStamp,Text,Media,avatar}) {
  return (
    <div className="post">
        <div className="postAvatar">
        <Avatar  />
        </div>
        <div className="postBody">
            <div className='postHeader'>
                <div className="postHeaderText">
                    <h3>
                     {displayName}{" "}
                    <span className="postheaderSpecial">
                        <VerifiedUser className="PostBadge"/>{Username}
                    </span>
                    </h3>
                </div>
                <div className='postHeaderDescription'>
                    {Text}
                </div>
            </div>
            <img src="https://pbs.twimg.com/profile_images/1488548719062654976/u6qfBBkF_400x400.jpg" alt=""/>
            <div className="postFooter">
                <ChatBubbleOutline fontSize="medium"/>
                <FavoriteBorder fontSize="medium" />
                <RepeatOutlined fontSize='medium'/>
                </div>  

        </div>
    </div>
  );
  }
export default Post
