    const [tweetMedia,setTweetmedia]=useState("");
 

  return (
    <div className="TweetBox">
        <form>
            <div className='TweetBoxInput'>
                <Avatar src="C:\Users\KIIT\Downloads\user.png"/>
                <input 
                onChange={(e)=>setTweet(e.target.value)}
                value={tweet}
                placeholder="What's Happening?"
                type="text"
