import './App.css';
import './darkMode.css';
import Feed from "./Feed";
import Sidebar from './Sidebar';
import Widgets from "./Widgets";
import React, { useState } from 'react';

function App() {
  const [theme, setTheme] = useState('light');
  const toggleTheme = () => {
      if (theme === 'light') {
        setTheme('dark');
      } else {
        setTheme('light');
    }
    };

  return (
    <div className="app">

      {/*sidebar*/}
      <Sidebar/>

      {/*Feed*/}
      <Feed></Feed>
      <Widgets/>
    </div>
  );
}

export default App;
